export const EasingFunctions = {
  // no easing, no acceleration
  linear: function (t) { return t },
  // accelerating from zero velocity
  easeInQuad: function (t) { return t*t },
  // decelerating to zero velocity
  easeOutQuad: function (t) { return t*(2-t) },
  // acceleration until halfway, then deceleration
  easeInOutQuad: function (t) { return t<.5 ? 2*t*t : -1+(4-2*t)*t },
  // accelerating from zero velocity
  easeInCubic: function (t) { return t*t*t },
  // decelerating to zero velocity
  easeOutCubic: function (t) { return (--t)*t*t+1 },
  // acceleration until halfway, then deceleration
  easeInOutCubic: function (t) { return t<.5 ? 4*t*t*t : (t-1)*(2*t-2)*(2*t-2)+1 },
  // accelerating from zero velocity
  easeInQuart: function (t) { return t*t*t*t },
  // decelerating to zero velocity
  easeOutQuart: function (t) { return 1-(--t)*t*t*t },
  // acceleration until halfway, then deceleration
  easeInOutQuart: function (t) { return t<.5 ? 8*t*t*t*t : 1-8*(--t)*t*t*t },
  // accelerating from zero velocity
  easeInQuint: function (t) { return t*t*t*t*t },
  // decelerating to zero velocity
  easeOutQuint: function (t) { return 1+(--t)*t*t*t*t },
  // acceleration until halfway, then deceleration
  easeInOutQuint: function (t) { return t<.5 ? 16*t*t*t*t*t : 1+16*(--t)*t*t*t*t }
};

export default function animate(duration, fn, ease_) {
  return new Promise(resolve => {
    let startTimestamp = null;
    let ease = ease_ || EasingFunctions.linear;

    const step = (timestamp) => {
      if (!startTimestamp) startTimestamp = timestamp;
      const progressedTime = timestamp - startTimestamp;

      const progressed = progressedTime / duration;

      fn(ease(progressed));

      if (progressedTime < duration) {
        window.requestAnimationFrame(step);
      } else {
        resolve();
      }
    };

    window.requestAnimationFrame(step);
  });

}

export function timeline(timeline) {
  for(let i=0; i<timeline.length; i++) {
    setTimeout(timeline[i+1], timeline[i]);
    i++;
  }
}
